import React, {useState} from 'react'
import './aplikacja.css'

function Aplikacja() {
    const [nazwaPozycji, setNazwaPozycji] = useState('')
    const [opisProduktu, setOpisProduktu] = useState('')
    const [select, setSelect] = useState('')
    const [cena, setCena] = useState('')

    const [nameArray, setNameArray] = useState([]);
    const [descriptionArray, setDescriptionArray] = useState([]);
    const [selectArray, setSelectArray] = useState([]);
    const [priceArray, setPriceArray] = useState([]);



 //   const [theArray, setTheArray] = useState([]);

  //  const addEntryClick = () => {
  //      setTheArray([...theArray, proba]);
  //  };

    const addToList = () => {
        setNameArray([...nameArray, nazwaPozycji]);
        setDescriptionArray([...descriptionArray, opisProduktu]);
        setSelectArray([...selectArray, select]);
        setPriceArray([...priceArray, cena]);
    }



  return (
    <div>
        <div className='formularz'>
            <form>
                <input
                    className='form-input'
                    type="text"
                    value={nazwaPozycji} 
                    onChange={e => setNazwaPozycji(e.target.value)}
                    placeholder="Nazwa Produktu" 
                />
                <input
                    className='form-input'
                    type="text"
                    value={opisProduktu} 
                    onChange={e => setOpisProduktu(e.target.value)}
                    placeholder="Opis (szczegóły) produktu" 
                />
                <select
                    onChange={e => setSelect(e.currentTarget.value)}
                    value={select}
                >
                    <option value="Podzespoły komputera">Podzespoły komputera</option>
                    <option value="Urządzenia peryferyjne">Urządzenia peryferyjne</option>
                    <option value="Oprogramowanie">Oprogramowanie</option>
                    <option value="Inne">Inne</option>
                </select>
                <input
                    className='form-input'
                    type="number"
                    value={cena}
                    onChange={e => setCena(e.target.value)}
                    placeholder="Cena Produktu"  
                />

                <input type="button" onClick={addToList} value="Add" />
            </form>
        </div> 



        {/*<input type="button" onClick={addEntryClick} value="Add" />
        <div>{theArray.map(entry =>
          <div>{entry}</div>
        )}
        </div>
        */}

        <div>
            <ul>{nameArray.map(nazwaproba =>
                <li>{nazwaproba}</li>)}
                {descriptionArray.map(opisproba =>
                <li>{opisproba}</li> )}
                {selectArray.map(katproba =>
                <li>{katproba}</li>)}
                {priceArray.map(cenaproba =>
                <li>{cenaproba}</li>)}
            </ul>
        </div>









    </div>
  )
}

export default Aplikacja
