import React, {useState, useEffect} from 'react'
import './aplikacja.css'

import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';
import { DragDropContext, Draggable, Droppable } from "react-beautiful-dnd";

import { ChevronDownIcon, ChevronUpIcon, PencilIcon, TrashIcon } from '@heroicons/react/outline'

function Aplikacja() {
    const [nazwaPozycji, setNazwaPozycji] = useState('')
    const [opisProduktu, setOpisProduktu] = useState('')
    const [select, setSelect] = useState('Podzespoły komputera')
    const [cena, setCena] = useState('')
    const [priceAll, setPriceAll] = useState(0)
    const [tablicaTablic, setTablicaTablic] = useState([]);
    const [value, setValue] = useState();

    const [selectFilter, setSelectFilter] = useState('Wszystko')

    const [cenaPodzespoly, setCenaPodzespoly] = useState(0)
    const [cenaUrzadzenia, setCenaUrzadzenia] = useState(0)
    const [cenaOprogramowanie, setCenaOprogramowanie] = useState(0)
    const [cenaInne, setCenaInne] = useState(0)

    const [iloscPodzespoly, setIloscPodzespoly] = useState(0);
    const [iloscUrzadzenia, setIloscUrzadzenia] = useState(0);
    const [iloscOprogramowanie, setIloscOprogramowanie] = useState(0);
    const [iloscInne, setIloscInne] = useState(0);





    const addToList = () => {

        setTablicaTablic([...tablicaTablic, [nazwaPozycji, opisProduktu, select, cena]]);
        console.log(tablicaTablic);

        setNazwaPozycji('');
        setOpisProduktu('');
        setSelect('Podzespoły komputera');
        setCena('');


    } 

    const addToAllPrice = (cena, select) => {
        setPriceAll(priceAll + parseInt(cena));

        if(select === 'Podzespoły komputera'){
            setCenaPodzespoly(cenaPodzespoly + parseInt(cena))
            setIloscPodzespoly(iloscPodzespoly + 1)
        } else if (select === 'Urządzenia peryferyjne'){
            setCenaUrzadzenia(cenaUrzadzenia + parseInt(cena))
            setIloscUrzadzenia(iloscUrzadzenia + 1)
        } else if (select === 'Oprogramowanie'){
            setCenaOprogramowanie(cenaOprogramowanie + parseInt(cena))
            setIloscOprogramowanie(iloscOprogramowanie + 1)
        } else if (select === 'Inne'){
            setCenaInne(cenaInne + parseInt(cena))
            setIloscInne(iloscInne + 1)
        }

    }

   const buttonDelete = (e, cenaUsun, katUsun) => {
    //console.log(e)
    tablicaTablic.splice(e, 1);

    setPriceAll(priceAll - cenaUsun);

    if(katUsun === 'Podzespoły komputera'){
        setCenaPodzespoly(cenaPodzespoly - cenaUsun)
        setIloscPodzespoly(iloscPodzespoly - 1)
    } else if (katUsun === 'Urządzenia peryferyjne'){
        setCenaUrzadzenia(cenaUrzadzenia - cenaUsun)
        setIloscUrzadzenia(iloscUrzadzenia - 1)
    } else if (katUsun === 'Oprogramowanie'){
        setCenaOprogramowanie(cenaOprogramowanie - cenaUsun)
        setIloscOprogramowanie(iloscOprogramowanie - 1)
    } else if (katUsun === 'Inne'){
        setCenaInne(cenaInne - cenaUsun)
        setIloscInne(iloscInne - 1)
    }


    //console.log(tablicaTablic);

    setValue({});

    }

    const buttonEdit = (nazwaEdytuj, opisEdytuj, katEdytuj, cenaEdytuj) => {
        setNazwaPozycji(nazwaEdytuj);
        setOpisProduktu(opisEdytuj);
        setSelect(katEdytuj);
        setCena(cenaEdytuj);
    }


    const generatePDF = () => {
        html2canvas(document.querySelector("#report")).then(canvas => {
            const imgData = canvas.toDataURL('image/png');
            const pdf = new jsPDF();
            pdf.addImage(imgData, 'PNG', 0, 0, 250, 0);
            pdf.save("download.pdf"); 
        });

    }

    const resetuj = () => {
        setTablicaTablic([]);
        setCenaPodzespoly(0);
        setCenaUrzadzenia(0);
        setCenaOprogramowanie(0);
        setCenaInne(0);
        setPriceAll(0);

        setIloscInne(0);
        setIloscOprogramowanie(0);
        setIloscPodzespoly(0);
        setIloscUrzadzenia(0);
    }

    const handleDragEnd = (e) => {
        if (!e.destination) return;
        let tempData = Array.from(tablicaTablic);
        let [source_data] = tempData.splice(e.source.index, 1);
        tempData.splice(e.destination.index, 0, source_data);
        setTablicaTablic(tempData);
      };





      const handleSortUpPrice = () => {
        tablicaTablic.sort((a,b) => {
            return b[3] - a[3]
        })
        setTablicaTablic(tablicaTablic);
        setValue({});
      }

      const handleSortDownPrice = () => {
        tablicaTablic.sort((a,b) => {
            return a[3] - b[3]
        })
        setTablicaTablic(tablicaTablic);
        setValue({});
      }

      const handleSortUpName = () => {
        tablicaTablic.sort(function(a,b) {
            if(a[0] < b[0]){
                return (-1)
            } if (a[0] > b[0]){
                return (1)
            } return (0)

        })
        setTablicaTablic(tablicaTablic);
        setValue({});
      }














  return (
    <div className='background'>
        <div className='formularz'>
            <form>
            <h1 className='form-h1'>Dodaj Produkt Do Listy</h1>
                <div className='input-label'>
                <label for="name" className='form-label'>Nazwa Pozycji</label>
                <input
                    id='name'
                    className='form-input'
                    type="text"
                    value={nazwaPozycji} 
                    onChange={e => setNazwaPozycji(e.target.value)}
                />
                </div>
                <div className='input-label'>
                 <label for="description" className='form-label'>Opis Pozycji</label>
                <input
                    id='description'
                    className='form-input'
                    type="text"
                    value={opisProduktu} 
                    onChange={e => setOpisProduktu(e.target.value)}
                />
                </div>
                <div className='input-label'>
                 <label for="select" className='form-label'>Kategoria Pozycji</label>
                <select
                    id='select'
                    onChange={e => setSelect(e.currentTarget.value)}
                    value={select}
                    className='form-select'
                >
                    <option value="Podzespoły komputera">Podzespoły komputera</option>
                    <option value="Urządzenia peryferyjne">Urządzenia peryferyjne</option>
                    <option value="Oprogramowanie">Oprogramowanie</option>
                    <option value="Inne">Inne</option>
                </select>
                </div>
                <div className='input-label'>
                <label for="price" className='form-label'>Cena Pozycji</label>
                <input
                    id='price'
                    className='form-input'
                    type="number"
                    value={cena}
                    onChange={e => setCena(e.target.value)} 
                />
                </div>

                <input type="button" onClick={() => {addToList(); addToAllPrice(cena, select)}} value="DODAJ" className='button-input'/>
            </form>
        </div> 


        {(() => {
            if (tablicaTablic.length > 0 ){
                return (        
        <div className='buttons-do-list'>
            <div className='selects'>
                <select
                className='select-filter' 
                onChange={e => setSelectFilter(e.currentTarget.value)}
                value={selectFilter}>
                    <option className="option-filter" value="Wszystko">Wszystko</option>
                    <option className="option-filter" value="Podzespoły komputera">Podzespoły komputera</option>
                    <option className="option-filter" value="Urządzenia peryferyjne">Urządzenia peryferyjne</option>
                    <option className="option-filter" value="Oprogramowanie">Oprogramowanie</option>
                    <option className="option-filter" value="Inne">Inne</option>
                </select>
            </div>
            <button onClick={generatePDF} type="button" className='button-export'>Export do PDF</button>
        </div>
        )} 
        })()}







        {(() => {
            if (tablicaTablic.length > 0 ){
                return (
                <div>

        {(() => {
            if (selectFilter === 'Wszystko' ){
                return (
                <div className='tabela-div'>
                    <DragDropContext onDragEnd={handleDragEnd}>
                <table id='report'>
                    <tr>
                        <th>Nazwa Produktu <button onClick={handleSortUpName}></button></th>
                        <th>Szczegóły Produktu</th>
                        <th>Kategoria</th>
                        <th className='th-icons'>Cena <div className='icons-sort'><ChevronUpIcon className='icon-sort' onClick={handleSortUpPrice}/><ChevronDownIcon className='icon-sort'onClick={handleSortDownPrice}/></div></th>
                        <th>Usuń</th>
                        <th>Edytuj</th>
                    </tr>
                    <Droppable droppableId="droppable-1">
                    {(provider) => (
                        <tbody
                            ref={provider.innerRef}
                            {...provider.droppableProps}
                        >
                    {tablicaTablic.map((printToList, index) =>
                        <Draggable
                        key={printToList[0]}
                        draggableId={printToList[0]}
                        index={index}
                      >
                    {(provider) => (

                    <tr {...provider.draggableProps} ref={provider.innerRef}>
                        <td {...provider.dragHandleProps}>{printToList[0]}</td>
                        <td {...provider.dragHandleProps}>{printToList[1]}</td>
                        <td {...provider.dragHandleProps}>{printToList[2]}</td>
                        <td {...provider.dragHandleProps}>{printToList[3]} PLN</td>

                        <td><button className="button-from-table" onClick={() => buttonDelete(index, printToList[3], printToList[2])}> <TrashIcon className='trash-icon'/> </button></td>

                        <td><button className="button-from-table" onClick={() => buttonEdit(printToList[0],printToList[1],printToList[2],printToList[3])}><PencilIcon className='trash-icon'/></button></td>
                    </tr>
                    )}
                     </Draggable>
                     )}
                    </tbody>
                    )}
                    </Droppable>
                    <tr>
                        <td className='elo'>{tablicaTablic.length}  
                        {(() => { if(tablicaTablic.length === 1){ 
                            return (<p>&nbsp;produkt</p>)
                        }else if (tablicaTablic.length < 5){
                            return (<p>&nbsp;produkty</p>)
                        } else {
                            return (<p>&nbsp;produktów</p>)
                        } })()}
                        </td>
                        <td></td>
                        <td></td>
                        <td>łącznie: {priceAll} PLN</td>
                    </tr>

                </table>
                </DragDropContext>
                </div>

        )} 
        })()}
        {(() => {
            if (selectFilter === 'Podzespoły komputera' ){
                return (
                <div className='tabela-div'>
                    <DragDropContext onDragEnd={handleDragEnd}>
                <table id='report'>
                    <tr>
                        <th>Nazwa Produktu</th>
                        <th>Szczegóły Produktu</th>
                        <th>Kategoria</th>
                        <th>Cena</th>
                        <th>Usuń</th>
                        <th>Edytuj</th>
                    </tr>
                    <Droppable droppableId="droppable-1">
                    {(provider) => (
                        <tbody
                            ref={provider.innerRef}
                            {...provider.droppableProps}
                        >
                    {tablicaTablic
                    .filter(printToList => printToList[2] === selectFilter)
                    .map((printToList, index) =>
                    <Draggable
                        key={printToList[0]}
                        draggableId={printToList[0]}
                        index={index}
                      >
                    {(provider) => (

                    <tr {...provider.draggableProps} ref={provider.innerRef}>
                        <td {...provider.dragHandleProps}>{printToList[0]}</td>
                        <td {...provider.dragHandleProps}>{printToList[1]}</td>
                        <td {...provider.dragHandleProps}>{printToList[2]}</td>
                        <td {...provider.dragHandleProps}>{printToList[3]} PLN</td>

                        <td><button className="button-from-table" onClick={() => buttonDelete(index, printToList[3], printToList[2])}> <TrashIcon className='trash-icon'/> </button></td>

                        <td><button className="button-from-table" onClick={() => buttonEdit(printToList[0],printToList[1],printToList[2],printToList[3])}><PencilIcon className='trash-icon'/></button></td>
                    </tr>
                     )}
                     </Draggable>
                     )}
                    </tbody>
                    )}
                    </Droppable>
                    <tr>
                        <td className='elo'>{iloscPodzespoly}  
                        {(() => { if(iloscPodzespoly === 1){ 
                            return (<p>&nbsp;produkt</p>)
                        }else if (iloscPodzespoly < 5){
                            return (<p>&nbsp;produkty</p>)
                        } else {
                            return (<p>&nbsp;produktów</p>)
                        } })()}
                        </td>
                        <td></td>
                        <td></td>
                        <td>łącznie: {cenaPodzespoly} PLN</td>
                    </tr>
                </table>
                </DragDropContext>
                </div>
        )} 
        })()}

        {(() => {
            if (selectFilter === 'Urządzenia peryferyjne' ){
                return (
                <div className='tabela-div'>
                    <DragDropContext onDragEnd={handleDragEnd}>
                <table id='report'>
                    <tr>
                        <th>Nazwa Produktu</th>
                        <th>Szczegóły Produktu</th>
                        <th>Kategoria</th>
                        <th>Cena</th>
                        <th>Usuń</th>
                        <th>Edytuj</th>
                    </tr>
                    <Droppable droppableId="droppable-1">
                    {(provider) => (
                        <tbody
                            ref={provider.innerRef}
                            {...provider.droppableProps}
                        >
                    {tablicaTablic
                    .filter(printToList => printToList[2] === selectFilter)
                    .map((printToList, index) =>
                    <Draggable
                        key={printToList[0]}
                        draggableId={printToList[0]}
                        index={index}
                      >
                    {(provider) => (

                    <tr {...provider.draggableProps} ref={provider.innerRef}>
                        <td {...provider.dragHandleProps}>{printToList[0]}</td>
                        <td {...provider.dragHandleProps}>{printToList[1]}</td>
                        <td {...provider.dragHandleProps}>{printToList[2]}</td>
                        <td {...provider.dragHandleProps}>{printToList[3]} PLN</td>

                        <td><button className="button-from-table" onClick={() => buttonDelete(index, printToList[3], printToList[2])}> <TrashIcon className='trash-icon'/> </button></td>

                        <td><button className="button-from-table" onClick={() => buttonEdit(printToList[0],printToList[1],printToList[2],printToList[3])}><PencilIcon className='trash-icon'/></button></td>
                    </tr>
                     )}
                     </Draggable>
                     )}
                    </tbody>
                    )}
                    </Droppable>
                    <tr>
                        <td className='elo'>{iloscUrzadzenia}  
                        {(() => { if(iloscUrzadzenia === 1){ 
                            return (<p>&nbsp;produkt</p>)
                        }else if (iloscUrzadzenia < 5){
                            return (<p>&nbsp;produkty</p>)
                        } else {
                            return (<p>&nbsp;produktów</p>)
                        } })()}
                        </td>
                        <td></td>
                        <td></td>
                        <td>łącznie: {cenaUrzadzenia} PLN</td>
                    </tr>
                </table>
                </DragDropContext>
                </div>
        )} 
        })()}

        {(() => {
            if (selectFilter === 'Oprogramowanie' ){
                return (
                <div className='tabela-div'>
                    <DragDropContext onDragEnd={handleDragEnd}>
                <table id='report'>
                    <tr>
                        <th>Nazwa Produktu</th>
                        <th>Szczegóły Produktu</th>
                        <th>Kategoria</th>
                        <th>Cena</th>
                        <th>Usuń</th>
                        <th>Edytuj</th>
                    </tr>
                    <Droppable droppableId="droppable-1">
                    {(provider) => (
                        <tbody
                            ref={provider.innerRef}
                            {...provider.droppableProps}
                        >
                    {tablicaTablic
                    .filter(printToList => printToList[2] === selectFilter)
                    .map((printToList, index) =>
                    <Draggable
                        key={printToList[0]}
                        draggableId={printToList[0]}
                        index={index}
                      >
                    {(provider) => (

                    <tr {...provider.draggableProps} ref={provider.innerRef}>
                        <td {...provider.dragHandleProps}>{printToList[0]}</td>
                        <td {...provider.dragHandleProps}>{printToList[1]}</td>
                        <td {...provider.dragHandleProps}>{printToList[2]}</td>
                        <td {...provider.dragHandleProps}>{printToList[3]} PLN</td>

                        <td><button className="button-from-table" onClick={() => buttonDelete(index, printToList[3], printToList[2])}> <TrashIcon className='trash-icon'/> </button></td>

                        <td><button className="button-from-table" onClick={() => buttonEdit(printToList[0],printToList[1],printToList[2],printToList[3])}><PencilIcon className='trash-icon'/></button></td>
                    </tr>
                     )}
                     </Draggable>
                     )}
                    </tbody>
                    )}
                    </Droppable>
                    <tr>
                        <td className='elo'>{iloscOprogramowanie}  
                        {(() => { if(iloscOprogramowanie === 1){ 
                            return (<p>&nbsp;produkt</p>)
                        }else if (iloscOprogramowanie < 5){
                            return (<p>&nbsp;produkty</p>)
                        } else {
                            return (<p>&nbsp;produktów</p>)
                        } })()}
                        </td>
                        <td></td>
                        <td></td>
                        <td>łącznie: {cenaOprogramowanie} PLN</td>
                    </tr>
                </table>
                </DragDropContext>
                </div>
        )} 
        })()}
        {(() => {
            if (selectFilter === 'Inne' ){
                return (
                <div className='tabela-div'>
                    <DragDropContext onDragEnd={handleDragEnd}>
                <table id='report'>
                    <tr>
                        <th>Nazwa Produktu</th>
                        <th>Szczegóły Produktu</th>
                        <th>Kategoria</th>
                        <th>Cena</th>
                        <th>Usuń</th>
                        <th>Edytuj</th>
                    </tr>
                    <Droppable droppableId="droppable-1">
                    {(provider) => (
                        <tbody
                            ref={provider.innerRef}
                            {...provider.droppableProps}
                        >
                    {tablicaTablic
                    .filter(printToList => printToList[2] === selectFilter)
                    .map((printToList, index) =>
                    <Draggable
                        key={printToList[0]}
                        draggableId={printToList[0]}
                        index={index}
                      >
                    {(provider) => (

                    <tr {...provider.draggableProps} ref={provider.innerRef}>
                        <td {...provider.dragHandleProps}>{printToList[0]}</td>
                        <td {...provider.dragHandleProps}>{printToList[1]}</td>
                        <td {...provider.dragHandleProps}>{printToList[2]}</td>
                        <td {...provider.dragHandleProps}>{printToList[3]} PLN</td>

                        <td><button className="button-from-table" onClick={() => buttonDelete(index, printToList[3], printToList[2])}> <TrashIcon className='trash-icon'/> </button></td>

                        <td><button className="button-from-table" onClick={() => buttonEdit(printToList[0],printToList[1],printToList[2],printToList[3])}><PencilIcon className='trash-icon'/></button></td>
                    </tr>
                     )}
                     </Draggable>
                     )}
                    </tbody>
                    )}
                    </Droppable>
                    <tr>
                        <td className='elo'>{iloscInne}  
                        {(() => { if(iloscInne === 1){ 
                            return (<p>&nbsp;produkt</p>)
                        }else if (iloscInne < 5){
                            return (<p>&nbsp;produkty</p>)
                        } else {
                            return (<p>&nbsp;produktów</p>)
                        } })()}
                        </td>
                        <td></td>
                        <td></td>
                        <td>łącznie: {cenaInne} PLN</td>
                    </tr>
                </table>
                </DragDropContext>
                </div>
        )} 
        })()}
        <div className='buttons-under-table'>
            <button className='button-reset' onClick={resetuj}>Resetuj</button>
        </div>
        </div>
        )} 
    })()}





    </div>
  )
}

export default Aplikacja
