import React, {useState} from 'react'
import './aplikacja.css'

import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';

import { PencilIcon, TrashIcon } from '@heroicons/react/outline'

function Aplikacja() {
    const [nazwaPozycji, setNazwaPozycji] = useState('')
    const [opisProduktu, setOpisProduktu] = useState('')
    const [select, setSelect] = useState('Podzespoły komputera')
    const [cena, setCena] = useState('')
    const [priceAll, setPriceAll] = useState(0)
    const [tablicaTablic, setTablicaTablic] = useState([]);
    const [value, setValue] = useState();

    const [selectFilter, setSelectFilter] = useState('Wszystko')


    const addToList = () => {

        setTablicaTablic([...tablicaTablic, [nazwaPozycji, opisProduktu, select, cena]]);
        console.log(tablicaTablic);

        setNazwaPozycji('');
        setOpisProduktu('');
        setSelect('Podzespoły komputera');
        setCena('');


    } 

    const addToAllPrice = () => {
        setPriceAll(priceAll + parseInt(cena));

    }

   const buttonDelete = (e, cenaUsun) => {
    //console.log(e)
    tablicaTablic.splice(e, 1);

    setPriceAll(priceAll - cenaUsun);

    console.log(tablicaTablic);

    setValue({});

    }

    const buttonEdit = (nazwaEdytuj, opisEdytuj, katEdytuj, cenaEdytuj) => {
        setNazwaPozycji(nazwaEdytuj);
        setOpisProduktu(opisEdytuj);
        setSelect(katEdytuj);
        setCena(cenaEdytuj);
    }


    const generatePDF = () => {
        html2canvas(document.querySelector("#report")).then(canvas => {
            const imgData = canvas.toDataURL('image/png');
            const pdf = new jsPDF();
            pdf.addImage(imgData, 'PNG', 0, 0, 250, 0);
            pdf.save("download.pdf"); 
        });

    }


  return (
    <div className='background'>
        <div className='formularz'>
            <form>
            <h1 className='form-h1'>Dodaj Produkt Do Listy</h1>
                <div className='input-label'>
                <label for="name" className='form-label'>Nazwa Pozycji</label>
                <input
                    id='name'
                    className='form-input'
                    type="text"
                    value={nazwaPozycji} 
                    onChange={e => setNazwaPozycji(e.target.value)}
                />
                </div>
                <div className='input-label'>
                 <label for="description" className='form-label'>Opis Pozycji</label>
                <input
                    id='description'
                    className='form-input'
                    type="text"
                    value={opisProduktu} 
                    onChange={e => setOpisProduktu(e.target.value)}
                />
                </div>
                <div className='input-label'>
                 <label for="select" className='form-label'>Kategoria Pozycji</label>
                <select
                    id='select'
                    onChange={e => setSelect(e.currentTarget.value)}
                    value={select}
                    className='form-select'
                >
                    <option value="Podzespoły komputera">Podzespoły komputera</option>
                    <option value="Urządzenia peryferyjne">Urządzenia peryferyjne</option>
                    <option value="Oprogramowanie">Oprogramowanie</option>
                    <option value="Inne">Inne</option>
                </select>
                </div>
                <div className='input-label'>
                <label for="price" className='form-label'>Cena Pozycji</label>
                <input
                    id='price'
                    className='form-input'
                    type="number"
                    value={cena}
                    onChange={e => setCena(e.target.value)} 
                />
                </div>

                <input type="button" onClick={() => {addToList(); addToAllPrice(cena)}} value="DODAJ" className='button-input'/>
            </form>
        </div> 



        <div className='buttons-do-list'>
            <select 
                onChange={e => setSelectFilter(e.currentTarget.value)}
                value={selectFilter}>
                    <option value="Wszystko">Wszystko</option>
                    <option value="Podzespoły komputera">Podzespoły komputera</option>
                    <option value="Urządzenia peryferyjne">Urządzenia peryferyjne</option>
                    <option value="Oprogramowanie">Oprogramowanie</option>
                    <option value="Inne">Inne</option>
            </select>
            <button onClick={generatePDF} type="button" className='button-export'>Export do PDF</button>
        </div>



        {(() => {
            if (selectFilter === 'Wszystko' ){
                return (
                <div className='tabela-div'>
                <table id='report'>
                    <tr>
                        <th>Nazwa Produktu</th>
                        <th>Szczegóły Produktu</th>
                        <th>Kategoria</th>
                        <th>Cena</th>
                        <th>Usuń</th>
                        <th>Edytuj</th>
                    </tr>
                    {tablicaTablic.map((printToList, index) =>
                    <tr>
                        <td>{printToList[0]}</td>
                        <td>{printToList[1]}</td>
                        <td>{printToList[2]}</td>
                        <td>{printToList[3]} PLN</td>

                        <td><button className="button-from-table" onClick={() => buttonDelete(index, printToList[3])}> <TrashIcon className='trash-icon'/> </button></td>

                        <td><button className="button-from-table" onClick={() => buttonEdit(printToList[0],printToList[1],printToList[2],printToList[3])}><PencilIcon className='trash-icon'/></button></td>
                    </tr>
                    )}
                    <tr>
                        <td className='elo'>{tablicaTablic.length}  
                        {(() => { if(tablicaTablic.length === 1){ 
                            return (<p>&nbsp;produkt</p>)
                        }else if (tablicaTablic.length < 5){
                            return (<p>&nbsp;produkty</p>)
                        } else {
                            return (<p>&nbsp;produktów</p>)
                        } })()}
                        </td>
                        <td></td>
                        <td></td>
                        <td>łącznie: {priceAll} PLN</td>
                    </tr>
                </table>
                </div>

        )} 
        })()}


        {(() => {
            if (selectFilter === 'Podzespoły komputera' ){
                return (
                <div className='tabela-div'>
                <table>
                    <tr>
                        <th>Nazwa Produktu</th>
                        <th>Szczegóły Produktu</th>
                        <th>Kategoria</th>
                        <th>Cena</th>
                        <th>Usuń</th>
                        <th>Edytuj</th>
                    </tr>
                    {tablicaTablic
